import React ,{ Component, PropTypes } from 'react';
import  Snake  from './components/Snake';
import  Food  from './components/Food';
import Board  from './components/Board';
import DebugState  from './components/DebugState';
import { UP, DOWN, LEFT, RIGHT } from './helpers/constants';
import { pluck } from './helpers/util';
import { Button ,Modal} from 'react-bootstrap';

const getDefaultState=(boardSize)=>{

    return {
        size: {
            board: boardSize,
            player: 5,
        },
        position:{

            food:{
            top:Math.floor(Math.random()*(boardSize.boardSize-10)),
            left:Math.floor(Math.random()*(boardSize.boardSize-10))
            },
            snake:{
                top:10,
                left:10,
                dir:LEFT,
            }
        },
        snakeSpeed:1,
        timeElapsed:0,
        snakeIndex:0,
        playerScore: 0,
        baseScore:10,
        gameOver:false,

    }
}



class Game extends Component{
constructor(props){
    super(props);
    const {boardSize}= props;
    this.state=getDefaultState({boardSize});
}

//  placeSnake=()=>{

//     const {player: playerPos}=this.state.position;
//     const side=pluck([UP,DOWN,LEFT,RIGHT]);

//     // generate snake object
//     const newSnake = this.increaseSnakeSize(playerPos,side)

//     this.setState({
//         ...this.state.position,
//         snake: newSnake
//     });
// }

handlePlayerCollision = () => {
    this.this.increaseSnakeSize(playerPos,side)
}

handleDebugToggle = () => {
    this.setState({
        debug: this.debug.checked
    });
}

increaseSnakeSize=(position,side)=>{
    console.log(this.state)
    this.setState({
        snakeIndex: this.state.snakeIndex + 1
    });

    const newSnake = { key: this.state.snakeIndex,dir:side};
    const { board, player } = this.state.size;

    switch(side) {
        case UP:
            newSnake.top = board;
            newSnake.left = position.left;
            break;
        case DOWN:
        newSnake.top = 0 - player;
        newSnake.left = position.left;
            break; 
        case LEFT:
        newSnake.top = position.top;
        newSnake.left = board;
            break;
        case RIGHT:
        newSnake.top = position.top;
        newSnake.left = 0 - player;
            break;
    }

    return newSnake;
}


startGame=()=>{
   // this.snakeInterval = setInterval(this.handleSnakePositions, 50);
    this.timeInterval = setInterval(this.updateGame, 1000);

}

updateGame=()=>{
    const { timeElapsed } = this.state;
    this.updateTimeAndScore();
    if(timeElapsed>0){
        if (timeElapsed % 3 === 0) {
            this.incrementSnakeSpeed();
        }
    }
}
updateTimeAndScore = () => {
    const { timeElapsed ,playerScore, baseScore} = this.state;

    this.setState({
        timeElapsed: timeElapsed + 1,
       playerScore: playerScore + baseScore,
    });
}

incrementSnakeSpeed=()=>{
   const { snakeSpeed } =this.state;
    this.setState({
        snakeSpeed:parseFloat((snakeSpeed + 0.25).toFixed(2))
    })
}

gameOver=()=>{
    const { gameOver }= this.state;

    clearInterval(this.gameInterval); 
    clearInterval(this.enemyInterval);
    clearInterval(this.timeInterval);

    this.setState({
        gameOver:true,
    });

}

resetGame = () => {
    const { boardSize, playerSize } = this.props;
    const { playerScore, highScore, globalHighScore, debug } = this.state;

    // clear intervals
    clearInterval(this.gameInterval); 
    clearInterval(this.enemyInterval);
    clearInterval(this.timeInterval);

    // if high score is higher than global high score, update it
    if (playerScore > globalHighScore) {
        this.updateGlobalHighScore(playerScore);
    }

    // reset state
    this.setState({
        ...getDefaultState({ boardSize, playerSize, highScore }),
        // persist debug state and high scores
        debug,
        highScore: playerScore > highScore ? playerScore : highScore,
        globalHighScore
    });
    // restart game
    this.startGame();

}

handleSnakePositions=(dirObj)=>{
    console.log(dirObj)
 const { snakeSpeed, position: { snake }}= this.state;
 //const board=size.board.boardSize;

        this.setState({
            position: {
                ...this.state.position,
                snake:((snake,board)=>{

                    // based on direction, increment the correct value (top / left)
                    switch(dirObj.dir) {
                        case UP:
                        if (snake.top === 0){
                            this.gameOver();
                        }
                        break;
                    case DOWN:
                    if (snake.top === board){
                        this.gameOver();
                    }
                        break;
                    case LEFT:
                    if (snake.left === 0){
                        this.gameOver();
                    }
                        break;
                    case RIGHT:
                    if (snake.left === board){
                        this.gameOver();
                    }
                        break;
                    }

                    snake.top= snake.top + (5*dirObj.top);
                    snake.left= snake.left + ( 5*dirObj.left);
                    return snake;

                  })(snake,this.state.size.board.boardSize)


            }

        });

    }
    render(){

        const { 
            size: { board, player }, 
            position: { player: playerPos },
            playerScore,
            timeElapsed
            //highScore,
            //globalHighScore
        } = this.state;
        return (
            <div>  
                 <Board dimension={this.state.size.board.boardSize}>

                 <Food food={this.state.position.food}/>

                 {(this.state.gameOver === true )&&
                 <gameOver>
                    <Modal.Dialog>
                        <Modal.Header closeButton>
                            <Modal.Title>Game Over</Modal.Title>
                        </Modal.Header>

                        <Modal.Body>
                            <p>Player Score: {playerScore}</p>
                        </Modal.Body>

                        <Modal.Footer>
                            <Button variant="secondary" onClick={this.resetGame}>Restart</Button>
                            <Button variant="primary">Close</Button>
                        </Modal.Footer>
                        </Modal.Dialog>
                 </gameOver>
                 }

                 <Snake key={this.state.position.snake.key}
                  info={this.state.position.snake}
                  playerPosition={playerPos}
                  handleSnakePositions={this.handleSnakePositions} 
                  onCollide={this.handlePlayerCollision} />

                  </Board>

                {/* {false && <p style={{ position: 'fixed', bottom: 0, left: 16 }}>Debug: <input type="checkbox" onChange={this.handleDebugToggle} ref={ n => this.debug = n }/></p>}
                {this.state.debug && <DebugState data={this.state} />} */}
            </div>

        )
    }
    componentDidMount() {
        this.startGame();
      //  this.fetchGlobalHighScore();
    }

    componentWillUnmount() {
       // clearInterval(this.state.gameInterval);
        clearInterval(this.state.snakeInterval);
        clearInterval(this.state.timeInterval);
    }
}
export default Game;
