        const { position, food: { top, left }} = this.props;
        console.log(this.props,left,)
        if ( position.left === left  ||
            position.top  === top ) {
            this.props.onCollide()
        }
    }

    render(){
        const {  food: { top, left }} = this.props;
    return (

        <Square 
       position={{ top, left }} />
    );
}
}

export default Food;
