import React, {Component,propTypes} from 'react';
import Square from './Square';

class Food extends Component {
    componentDidUpdate() {
        const { size, position:{snake}, food: { top, left }} = this.props;

        if ( snake.left < (left + size) && 
        snake.top  < (top + size)  &&
            (playerPosition.left + size) > left &&
            (playerPosition.top  + size) > top) {

            this.props.onCollide()
        }
    }

    render(){
        const {  food: { top, left }} = this.props;
    return (

        <Square 
       position={{ top, left }} />
    );
}
}

export default Food;
